import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { changeField, initializeForm, login,test } from '../../modules/auth';
import SignIn from '../../pages/SingIn'
import { check } from '../../modules/user';

const LoginAuthForm = ({ history }) => {
  const [error, setError] = useState(null);
  const dispatch = useDispatch(); //리덕스 스토어에 접근
  const { form, loginAuth, authError, user } = useSelector(({ auth, user }) => ({
    form: auth.login,
    loginAuth: auth.loginAuth,
    authError: auth.authError,
    user: user.user,
  })); //리덕스가 가지고있는 상태값
  // 인풋 변경 이벤트 핸들러
  const onChange = e => {
    const { value, name } = e.target;
    dispatch(
      changeField({
        form: 'login',
        key: name,
        value,
      }),
    );
  };


  // 폼 등록 이벤트 핸들러
  const onSubmit = e => {
    e.preventDefault();
    const { id, password } = form;
    dispatch(login({ id, password }));
  };

  // 컴포넌트가 처음 렌더링 될 때 form 을 초기화함
  useEffect(() => {
    dispatch(initializeForm('login'));
  }, [dispatch]);

  useEffect(() => {
   // consol.log("??");
    if (authError) {
      console.log("authError : "+authError.status);
      if(authError.response.status == 401){
        console.log(authError.response);
      }
      //alert("네트워크 오류가 발생했습니다.\n관리자에게 문의해주세요");
      setError('로그인 실패');
      return;
    }
    if (loginAuth) {
      console.log('로그인 성공');
      dispatch(check());
    }
  }, [loginAuth, authError, dispatch]);

  useEffect(() => {
    if (user) {
      history.push('/register');
      try{
        localStorage.setItem('user',JSON.stringify(user));
      }catch(e){
        console.log('localStorage is not working');
      }
    }
  }, [history, user]);

  return (
    <SignIn
      type="login"
      form={form}
      onChange={onChange}
      onSubmit={onSubmit}
      error={error}
    />
  );
};

export default withRouter(LoginAuthForm);
