import React , {useRef}from "react";
import styled from "styled-components";
import palette from "../../lib/styles/palette";
import Button from "../../components/common/Button";
import Selector from "react-select";

/**
 * 스타일링된 input
 */
const StyledInput = styled.input`
  font-size: 1rem;
  display: table-cell;
  border: none;
  color: black;
  width: 360px;
  border: 1px solid ${palette.gray[5]};
  padding: 0.5rem;
  outline: none;
  margin-bottom: 5rem;
  &:focus {
    color: $oc-teal-7;
  }
`;
const ButtonWithMarginTop = styled(Button)`
  margin-top: 1rem;
  position: absoulte;
  width: auto;
  height: 50px;
  margin: 1rem;
`;
const techCompanies = [
  { label: "박윤범", value: 1 },
  { label: "개룐", value: 2 },
  { label: "입니다", value: 3 }
];
/**
 * 폼 하단에 로그인 혹은 회원가입 링크를 보여줌
 */
const Footer = styled.div`
  margin-top: 2rem;
  text-align: right;
  a {
    color: ${palette.gray[6]};
    text-decoration: underline;
    &:hover {
      color: ${palette.gray[9]};
    }
  }
`;
/**
 * 에러를 보여줍니다
 */
const ErrorMessage = styled.div`
  color: red;
  text-align: center;
  font-size: 0.875rem;
  margin-top: 1rem;
`;
const FirstRegisterPage = ({ onSubmit, onChange, form, error }) => {
  return (
    <div>
      <div style={{ float: "right" }}>
        <ButtonWithMarginTop
          onClick={e => onSubmit(e, e.target.id)}
          type="submit"
          id="temporarySave"
          form="myform"
          cyan
          fullWidth
          style={{ marginTop: "1rem" }}
        >
          {"임시저장"}
        </ButtonWithMarginTop>
      </div>
      <form onSubmit={e=>onSubmit(e,'firstNext')} id="myform">
      <strong>아이디</strong>
        <StyledInput
          autoComplete="username"
          name="username"
          placeholder="아이디"
          onChange={onChange}
          value={form.username}
        />
        <strong>비밀번호</strong>
        <StyledInput
          autoComplete="new-password"
          name="password"
          placeholder="비밀번호"
          type="password"
          onChange={onChange}
          value={form.password}
        />
        <StyledInput
          autoComplete="new-password"
          name="passwordConfirm"
          placeholder="비밀번호 확인"
          type="password"
          onChange={onChange}
          value={form.passwordConfirm}
        />
        <strong>사업자등록번호</strong>
        <StyledInput
          autoComplete="businessLicenseNumber"
          name="businessLicenseNumber"
          placeholder="사업자등록번호"
          onChange={onChange}
          value={form.businessLicenseNumber}
        />
        <strong>사업자명</strong>
        <StyledInput
          autoComplete="businessOperatorName"
          name="businessOperatorName"
          placeholder="사업자명"
          onChange={onChange}
          value={form.businessOperatorName}
        />
        <Selector placeholder="판매상품 카테고리" options={techCompanies} />
        {error && <ErrorMessage>{error}</ErrorMessage>}
        <div style={{ float: "right" }}>
          <ButtonWithMarginTop cyan fullWidth style={{ marginTop: "1rem" }}>
            {"이전"}
          </ButtonWithMarginTop>
          <ButtonWithMarginTop cyan fullWidth style={{ marginTop: "1rem" }}>
            {"다음"}
          </ButtonWithMarginTop>
        </div>
      </form>
      <Footer>
      </Footer>
    </div>
  );
};
export default FirstRegisterPage;
